import {Component, OnInit} from '@angular/core';
import {FormControl, FormGroup} from "@angular/forms";
import {Cliente} from "../../Cliente";
import {ClientesService} from "../../clientes.service";

@Component({
  selector: 'app-clientes',
  templateUrl: './clientes.component.html',
  styleUrls: ['./clientes.component.css']
})
export class ClientesComponent implements OnInit {

  formulario: any;
  tituloFormulario: string = "Formulário de Clientes";

  constructor(private clienteService: ClientesService) {
  }

  ngOnInit() {
    this.tituloFormulario = "Formulário de Clientes";
    this.formulario = new FormGroup({
      nome: new FormControl(null),
      sobrenome: new FormControl(null),
      documento: new FormControl(null),
      pais: new FormControl(null),
      endereco: new FormControl(null),
      carro: new FormControl(null),
      carroId: new FormControl(null)
    });
  }

  EnviarFormulario(): void {
    const cliente: Cliente = this.formulario.value;

    this.clienteService.createCliente(cliente).subscribe(resultado => {
      alert("Cliente cadastrado com sucesso!");
    });
  }
}
<div class="container d-flex justify-content-center align-items-center vh-100">
  <div *ngIf="formulario" class="col-6 border rounded p-5 shadow">
    <h6 class="text-center">{{tituloFormulario}}</h6>
    <hr/>

    <form [formGroup]="formulario" (ngSubmit)="EnviarFormulario()">
      <div class="form-group">
        <label>Nome</label>
        <input class="form-control form-control-sm" formControlName="nome" type="text">
      </div>
      <div class="form-group">
        <label>Sobrenome</label>
        <input class="form-control form-control-sm" formControlName="sobrenome" type="text">
      </div>
      <div class="form-group">
        <label>Endereço</label>
        <input class="form-control form-control-sm" formControlName="endereco" type="text">
      </div>
      <div class="form-group">
        <label>País</label>
        <input class="form-control form-control-sm" formControlName="pais" type="text">
      </div>
      <hr>
      <div class="form-group">
        <h5>Carro:</h5>
      </div>
      <div class="form-group">
        <label>Carro</label>
        <input class="form-control form-control-sm" formControlName="carro" type="text">
      </div>
      <div class="text-center mt-4 m-auto">
        <button class="btn btn-sm btn-secondary" type="submit">Registrar</button>
        <button class="btn btn-sm btn-light ml-2" type="button">Voltar</button>
      </div>
    </form>
  </div>
</div>
